import React, { useEffect } from 'react'
import styled from 'styled-components'
import { ContainerCustom, ContentBlock, SectionRowAvatar, MainContent, Section, SectionRow, SignedOutContainer,ContainerCustomTokenListSections,ItemListSection, ItemSelectionSection } from '../components/base/base'
import { TokensList } from '../components/TokensList/TokensList'
import { ItemSelection } from '../components/ItemSelection/ItemSelection'

import { Title, DomainTitle } from '../typography/Title'
import { Text } from '../typography/Text'

import { AccountButton } from '../components/account/AccountButton'

import { DomainSelectBoxContainer } from '../components/base/base'
import { DomainTitleContainer } from '../components/base/base'
import { CHAIN_NAMES, useEthers } from '@usedapp/core'
import { useBadgeList } from '../portalHooks/useBadgeList'
import { MetaBadge } from '../portalHooks/BadgeHelperTypes'
import {Form} from 'react-bootstrap'
import { updatedSelectedItem } from '../components/TokensList/SelectedItemSlice'
import { useAppDispatch } from '../hooks'
import { RANDOM_VISOR_COLORS } from '../portalHooks/BadgeHelper'
import {Badge } from 'react-bootstrap'

export function Tokens() {
  const { chainId, account, library } = useEthers();
  const badges: MetaBadge[]| undefined = useBadgeList(account);

  const dispatch = useAppDispatch();

  const handleApplyBadgeToAvatar =(index: number) =>  {  
    dispatch (updatedSelectedItem(
      {
        index: index,
        balance: 0,
        holders: 0,
        name: 'noname',
        address: 'noaddress',
        color: null,
      }
    ))
  }

  useEffect(() => {
    // clearing badge selection when
    // account ORR chainID changed
    handleApplyBadgeToAvatar(0);

  }, [account,chainId])

  return (

    // <div style={{display: 'flex',overflow:'scroll' }}>

