    //          <p>Badges based on <b>on-chain activity</b></p>
    //          {chainId && <Badge pill bg="dark">{CHAIN_NAMES[chainId]}</Badge>}
    //        </DomainTitleContainer>}

    //        {account && <DomainSelectBoxContainer>
    //          <Form.Select aria-label="Default select example">
    //            <option>Meta Portal</option>
    //          </Form.Select>
    //        </DomainSelectBoxContainer>}

    //      </SectionRow> 

    //    { account &&  <ItemsContentBlock>
    //        <TokensList />
    //      </ItemsContentBlock>}

    //    </ItemListSection>

    //    <ItemSelectionSection>
    //      <SectionRowAvatar>
    //       { account && <SignerXp>{badges ? (badges.length * 10): 0} XP</SignerXp>}
    //      {account && <AccountButton />}
    //      </SectionRowAvatar>


    //      {account && <ItemsContentBlock>
    //        <ItemSelection />
    //      </ItemsContentBlock>}
    //    </ItemSelectionSection>
    //  </ContainerCustomTokenListSections>)
    // }


    // </div>

    <BadgesPageContainer>

      <BadgeListColumn>

        <BadgeListHeaderRow>
          <div>
          <p>Badges based on <b>on-chain activity</b></p>
          </div>
          <Form.Select aria-label="Default select example">
            <option>Meta Portal</option>
          </Form.Select>

        </BadgeListHeaderRow>

        <BadgeListRow>
          <TokensList />
        </BadgeListRow>

      </BadgeListColumn>



      <AvatarColumn>

        <AvatarHeaderRow>

        </AvatarHeaderRow>

      </AvatarColumn>

    </BadgesPageContainer>

  )
}

const ItemsContentBlock = styled(ContentBlock)`
  overflow: scroll;
`

const SignerXp = styled.p`
  font-size:18px;
`

const BadgesPageContainer = styled.div `
  display: flex;
`
const BadgeListColumn = styled.div `
  display: flex;
  flex-direction: column;

`

const AvatarColumn = styled.div `
  display: flex;
  flex-direction: column;
`

const BadgeListHeaderRow = styled.div `
  display: flex;
  justify-content: 'space-around'
`

