import React, { useEffect } from 'react'
import styled from 'styled-components'
import { ContainerCustom, ContentBlock, ContentBlockBackground, MainContent, Section, SectionRow, SignedOutContainer,ContainerCustomTokenListSections,ItemListSection, ItemSelectionSection } from '../components/base/base'
import { TokensList } from '../components/TokensList/TokensList'
import { ItemSelection } from '../components/ItemSelection/ItemSelection'

import { Title, DomainTitle } from '../typography/Title'

import { AccountButton } from '../components/account/AccountButton'

import { DomainSelectBoxContainer } from '../components/base/base'
import { DomainTitleContainer } from '../components/base/base'
import { useEthers } from '@usedapp/core'
import { useBadgeList } from '../portalHooks/useBadgeList'
import { MetaBadge } from '../portalHooks/BadgeHelperTypes'
import {Form} from 'react-bootstrap'
import { updatedSelectedItem } from '../components/TokensList/SelectedItemSlice'
import { useAppDispatch } from '../hooks'
import { RANDOM_VISOR_COLORS } from '../portalHooks/BadgeHelper'
import {Badge } from 'react-bootstrap'

export function Tokens() {
  const { chainId, account, library } = useEthers();
  const badges: MetaBadge[]| undefined = useBadgeList(account);

  const dispatch = useAppDispatch();

  const handleApplyBadgeToAvatar =(index: number) =>  {  
    dispatch (updatedSelectedItem(
      {
        index: index,
        balance: 0,
        holders: 0,
        name: 'noname',
        address: 'noaddress',
        color: null,
      }
    ))
  }

  useEffect(() => {
    // clearing badge selection when
    // account ORR chainID changed
    handleApplyBadgeToAvatar(0);

  }, [account,chainId])

  return (

    <div style={{display: 'flex'}}>
      <div>hello</div>
               <SignedOutContainer>
             {!account && (
              <div>
                <p>Connect your browser wallet to access the portal.</p> <AccountButton />
                </div>
              )}

             </SignedOutContainer>

     <MainContent>


      <ContainerCustomTokenListSections>

        <ItemListSection>  

          <SectionRow>
            {account && <DomainTitleContainer>
              <p>Badges based on <b>on-chain activity</b></p>
              <Badge pill bg="dark">Ethereum Mainnet Only</Badge>
            </DomainTitleContainer>}

            {account && <DomainSelectBoxContainer>
              <Form.Select aria-label="Default select example">
                <option>Meta Portal</option>
                <option value="1">All</option>
              </Form.Select>
            </DomainSelectBoxContainer>}

          </SectionRow> 
        { account &&  <ItemsContentBlock>
            <TokensList />
          </ItemsContentBlock>}
        </ItemListSection>
        <ItemSelectionSection>
          <SectionRow>
           { account && <SignerXp>{badges ? (badges.length * 10): 0} XP</SignerXp>}
          {account && <AccountButton />}

          </SectionRow>
          {account && <ItemsContentBlock>
            <ItemSelection />
          </ItemsContentBlock>}
        </ItemSelectionSection>
      </ContainerCustomTokenListSections>
    </MainContent>
    </div>

  )
}

const ItemsContentBlock = styled(ContentBlock)`
  overflow: scroll;
`

const SignerXp = styled.p`
  font-size:18px;
`
