import { formatUnits } from '@ethersproject/units'
import uniswapToken from '@uniswap/default-token-list'
import { ChainId, ERC20Interface, useContractCall, useContractCalls, useContractFunction, useEthers, useSendTransaction } from '@usedapp/core'
import React, { useEffect, useState } from 'react'
import { ERC1155Interface, VISORSInterface } from '../../interfaces/interfaces'
import styled from 'styled-components'
import { Colors } from '../../global/styles'
import { TextBold } from '../../typography/Text'
import { TokenIcon } from './TokenIcon'
import { Col, Container, Row } from 'react-bootstrap'
import { TokenSVGIcon } from './TokenSVGIcon'
import { useAppSelector, useAppDispatch } from '../../hooks'
import { increment } from '../../features/counter/counterSlice'
import { updatedSelectedItem } from './SelectedItemSlice'

import { ContractFactory, ethers, utils } from 'ethers'
import axios, { AxiosResponse } from 'axios'
import {Contract} from '@ethersproject/contracts'
import { BadgeOverviewModal } from '../badge/BadgeOverviewModal'
import { parseResponseForItems, getSuccessfulTransactionCount, parseForBadgesForTypes, RANDOM_VISOR_COLORS } from './BadgeHelper'
import { getTransactionsFor } from './TransactionsAPI'
import { useBadgeList } from '../../portalHooks/useBadgeList'

// import { useContractFunction } from '../../workarounds/useContractFunction'

declare const window: any;

const initBadges: any[] = [];

export const VisorsABI = [
  "function requestMint(uint256 visorId) public payable",
];

function getTokenList(chainId?: ChainId) {
  return uniswapToken.tokens.filter((token) => token.chainId == chainId)
}



export function TokensList() {

  const { chainId, account, library } = useEthers();
  console.log('chainId : ',chainId)
  const badges = useBadgeList(account);
  // const [badges, setBadges] = useState(initBadges);
  const [showModal, setShowModal] = useState(false)

  let mountedRef = true;
  const visorInterface = new utils.Interface(VisorsABI)
  const VisorsContractAddress = '0x2f72199e3721c3b1eeb2913089c14c72ee9036b0'
  const signer = library?.getSigner();
  const contract = new Contract(VisorsContractAddress, visorInterface, signer);
  const { send } = useContractFunction(contract, 'requestMint', { signer: signer,  transactionName: 'Mint' })


  const doMint = async (visorId: number) => {
    let overrides = {
      value: ethers.utils.parseEther('0.0001')
    };
    send([1],overrides);
  }  



  useEffect(() => {
    const doAsync = async () => {
      init();
    }
    if(mountedRef) {
      doAsync();
    }
    return () => {
      mountedRef = false;
    }; 


  }, [mountedRef]);

  const init = async () => {
    console.log('account : ',account);
    if(!account || account === undefined){
      return;
    }
    console.log('awaiting response....')
   const response =  await getTransactionsFor(account);  
   console.log('axios : ',response);

    if(!response){
      return;
    }

    const items = parseResponseForItems(response);
    if(!items) {
      return;
    }
    const successCount = getSuccessfulTransactionCount(items);

    console.log('successes : ',successCount);
    const badges = parseForBadgesForTypes([successCount]);
    console.log('badges : ',badges);
    loadBadges(badges);
  }

    const selectedItemIndex = useAppSelector((state) => {
      return state.selectedItem.index;
    })
    const dispatch = useAppDispatch();

    const handleItemSelect =(index: number, balance: number, holders: number,name: string, address: string) =>  {

      dispatch (updatedSelectedItem(
        {
          index: index,
          balance: balance,
          holders: holders,
          name: name,
          address: address,
          color: index >= 0 ? RANDOM_VISOR_COLORS[index] : null,
        }
      ))

    }


    const loadBadges =(transactionItems: any[]): void => {
      setBadges(transactionItems);
    }


  return (
    <div>
      {showModal && <BadgeOverviewModal setShowModal={setShowModal} />}
      {badges.length === 0 ? (<div>Not badges available...</div> ): (
        <div>
        <Row>
        {badges.map((badge: any, idx:number) => {
              return (
              <Col sm={4} md={2} lg={2} key={`parentSection-${idx}`}> 
              <Container>
              <Row>
                  <Col key={`SubSection-${idx}`}>
                    <TokenItem > 
                    <TokenIconContainer onClick={() => setShowModal(!showModal)}>
                    {/* <TokenIconContainer onClick={() => handleItemSelect(idx,idx ,11,`Element ${idx}`,'address')}> */}
                      <TokenSVGIcon src={''} alt={''}></TokenSVGIcon>
                      <TokenBalance> 
                        {formatUnits(0,0)}
                      </TokenBalance>
                    </TokenIconContainer>
                    <TokenName>{badge.badgeName}</TokenName>
                    <MintButton 

                      onClick={() => doMint(idx)}

                    >Mint</MintButton>
                  </TokenItem>
                  </Col>
              </Row>
              </Container>
              </Col>
          )})}
        </Row>
      </div>
    )}
    </div>
  )
}


export const Button = styled.button`
  display: grid;
  grid-auto-flow: column;
  grid-column-gap: 8px;
  align-items: center;
  width: fit-content;
  height: 40px;
  font-size: 14px;
  line-height: 24px;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: ${Colors.Black[900]};
  border: 1px solid ${Colors.Black[900]};
  background-color: transparent;
  cursor: pointer;

  &:hover,
  &:focus {
    background-color: ${Colors.Black[900]};
    color: ${Colors.Yellow[100]};
  }
`

const MintButton = styled(Button)`
  background-color: ${Colors.Yellow[100]};
`

const List = styled.ul`
`

const ListItem = styled.li`
`

const TokenItem = styled.li`
  display: flex;
  flex-direction: column;
`

const TokenIconContainer = styled.div`
  display: flex;
  justify-content: center;
  width: 120px;
  height:120px;
  margin: 0.2em;
  border: 1px solid #ebebeb};
  background: rgb(60, 60, 60);
  background: rgba(149, 149, 149, 0.4);
  position: relative;

  &:hover,
  &:focus, 
  &:active {
    color: ${Colors.Yellow[100]};
    border-width: medium;
    border-color: ${Colors.Yellow[100]};
  }
`

const TokenName = styled(TextBold)`
  margin-left: 4px;
  font-size: 12px;

`

const TokenTicker = styled(TextBold)`
  grid-area: ticker;
  color: ${Colors.Gray[600]};
`

const TokenBalance = styled(TextBold)`
  position: absolute;
  bottom: 0;
  right: 0;
  padding:0.25em;
  font-size: 15px;
`
