import { formatUnits } from '@ethersproject/units'
import { useContractCall, useEthers } from '@usedapp/core'
import React, { useState } from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import styled from 'styled-components'
import { Colors } from '../../global/styles'
import { ERC1155Interface } from '../../interfaces/interfaces'
import { Badge } from '../../portalHooks/BadgeHelperTypes'
import { useBadgeList } from '../../portalHooks/useBadgeList'
import { TextBold } from '../../typography/Text'
import { BadgeOverviewModal, BadgeParams } from '../badge/BadgeOverviewModal'
import { TokenSVGIcon } from './TokenSVGIcon'

declare const window: any;


export function useERC1155TokenBalance(tokenAddress: string,  addresses: (string|undefined|null)[], tokenIds: number[]): any[] {
  const [tokenBalances] =
    useContractCall(
        addresses &&
        tokenAddress && {
          abi: ERC1155Interface,
          address: tokenAddress,
          method: 'balanceOfBatch',
          args: [addresses, tokenIds],
        }
    ) ?? []
  return tokenBalances
}


export function TokensList() {

  const { chainId, account, library } = useEthers();
  const badges: Badge[]| undefined = useBadgeList(account);

  console.log('badges : ',badges)
  const [showModal, setShowModal] = useState(false)
  const [selectedBadgeParams, setSelectedBadgeParams] = useState<BadgeParams>()

    const handleShowModal =(index: number, badge: Badge) =>  {
      if(!badge || !badge.ui){
        return;
      }
      setSelectedBadgeParams({
        name: badge.ui.name,
        description: badge.ui.description,
        requirements: badge.ui.requirements,
        address: badge.ui.onChain ? badge.ui.onChain.address : null,
        tokenId: badge.ui.onChain ? badge.ui.onChain.tokenId : null
      });

      setShowModal(!showModal);
    }



  // const attachBalancesToBadges =(badges: Badge[], account: string)=> {

  //   let addresses = [];
  //   let tokenIds = [];
  //   let accounts = [];
  //   badges.forEach((badge: Badge)=> {
  //     if(!badge.ui || !badge.ui.onChain) {
  //       return;
  //     }
  //     addresses.push(badge.ui.onChain.address);
  //     tokenIds.push(badge.ui.onChain.tokenId);
  //     accounts.push(badge.ui.onChain.accounts);
  //   });
  //   const balances = useERC1155TokenBalance(
  //      addresses,
  //      tokenIds,
  //      accounts,
  //      );
  // }


  return (
    <div>
      {
      (showModal && selectedBadgeParams) && <BadgeOverviewModal setShowModal={setShowModal} badgeParams={selectedBadgeParams} />
      }
      {badges && badges.length === 0 ? (<div>Not badges available...</div> ): (
        <div>
        <Row>
        {badges && badges.map((badge: Badge, idx:number) => {
              return (
              <Col sm={4} md={2} lg={2} key={`parentSection-${idx}`}> 
              <Container>
              <Row>
                  <Col key={`SubSection-${idx}`}>
                    <TokenItem > 
                    <TokenIconContainer onClick={() => handleShowModal(idx, badge)}>
                    {/* <TokenIconContainer onClick={() => handleItemSelect(idx,idx ,11,`Element ${idx}`,'address')}> */}
                      <TokenSVGIcon src={''} alt={''}></TokenSVGIcon>
                      <TokenBalance> 
                      {badge.ui && <TokenName>{badge.ui.name}</TokenName>}                    

                        {formatUnits(10,0)}xp
                      </TokenBalance>
                    </TokenIconContainer>
                  </TokenItem>
                  </Col>
              </Row>
              </Container>
              </Col>
          )})}
        </Row>
      </div>
    )}
    </div>
  )
}


export const Button = styled.button`
  display: grid;
  grid-auto-flow: column;
  grid-column-gap: 8px;
  align-items: center;
  width: fit-content;
  height: 40px;
  font-size: 14px;
  line-height: 24px;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: ${Colors.Black[900]};
  border: 1px solid ${Colors.Black[900]};
  background-color: transparent;
  cursor: pointer;

  &:hover,
  &:focus {
    background-color: ${Colors.Black[900]};
    color: ${Colors.Yellow[100]};
  }
`

const MintButton = styled(Button)`
  background-color: ${Colors.Yellow[100]};
`

const TokenItem = styled.li`
  display: flex;
  flex-direction: column;
`

const TokenIconContainer = styled.div`
  display: flex;
  justify-content: center;
  width: 120px;
  height:120px;
  margin: 0.2em;
  border: 1px solid #ebebeb};
  background: rgb(60, 60, 60);
  background: rgba(149, 149, 149, 0.4);
  position: relative;

  &:hover,
  &:focus, 
  &:active {
    color: ${Colors.Yellow[100]};
    border-width: medium;
    border-color: ${Colors.Yellow[100]};
  }
`

const TokenName = styled(TextBold)`
  margin-left: 4px;
  font-size: 12px;
`

const TokenTicker = styled(TextBold)`
  grid-area: ticker;
  color: ${Colors.Gray[600]};
`

const TokenBalance = styled(TextBold)`
  position: absolute;
  bottom: 0;
  right: 0;
  padding:0.25em;
  font-size: 12px;
`
