    //     </Section>
    //   </ContainerCustom>) :
    //  (<ContainerCustomTokenListSections>

    //    <ItemListSection>  

    //      <SectionRow>
    //        {account && <DomainTitleContainer>
    //          <p>Badges based on <b>on-chain activity</b></p>
    //          {chainId && <Badge pill bg="dark">{CHAIN_NAMES[chainId]}</Badge>}
    //        </DomainTitleContainer>}

    //        {account && <DomainSelectBoxContainer>
    //          <Form.Select aria-label="Default select example">
    //            <option>Meta Portal</option>
    //          </Form.Select>
    //        </DomainSelectBoxContainer>}

    //      </SectionRow> 

    //    { account &&  <ItemsContentBlock>
    //        <TokensList />
    //      </ItemsContentBlock>}

    //    </ItemListSection>

    //    <ItemSelectionSection>
    //      <SectionRowAvatar>
    //       { account && <SignerXp>{badges ? (badges.length * 10): 0} XP</SignerXp>}
    //      {account && <AccountButton />}
    //      </SectionRowAvatar>


    //      {account && <ItemsContentBlock>
    //        <ItemSelection />
    //      </ItemsContentBlock>}
    //    </ItemSelectionSection>
    //  </ContainerCustomTokenListSections>)
    // }


    // </div>
    <div>

    { !account ? (<BadgesPageContainer>

      <BadgeListColumn>

        <BadgeListHeaderRow>
          <BadgeListHeaderInfoColumn>
            <p>Badges based on <b>on-chain activity</b></p>
            {chainId && <Badge pill bg="dark">{CHAIN_NAMES[chainId]}</Badge>}          
            </BadgeListHeaderInfoColumn>
          <BadgeListHeaderDropdownColumn>
            <Form.Select aria-label="Default select example">
              <option>Meta Portal</option>
            </Form.Select>
          </BadgeListHeaderDropdownColumn>
        </BadgeListHeaderRow>
        <BadgeListRow>
          <TokensList />
        </BadgeListRow>


      </BadgeListColumn>



      <AvatarColumn>

        <AvatarHeaderRow>

          <AvatarHeaderXPColumn>
          { account && <SignerXp>{badges ? (badges.length * 10): 0} XP</SignerXp>}          
          </AvatarHeaderXPColumn>

          <AvatarHeaderAccountSignInColumn>
              <AccountButton />
          </AvatarHeaderAccountSignInColumn>

        </AvatarHeaderRow>
        <AvatarRow>
          <ItemSelection />
        </AvatarRow>
      </AvatarColumn>

    </BadgesPageContainer>) : 
    (<BadgesPageContainer>
      <Text style={{margin: '0.5em'}}> Connect browser wallet to access portal</Text>
      <AccountButton />
    </BadgesPageContainer>)
  }
  </div>
  )
}

const ItemsContentBlock = styled(ContentBlock)`
  overflow: scroll;
`

const SignerXp = styled.p`
  font-size:18px;
`

const BadgesPageContainer = styled.div `
  display: flex;
  margin: 1em;
  column-gap: 10px;
  flex-flow: wrap-reverse;


`
const BadgeListColumn = styled.div `
  display: flex;
  flex-direction: column;
  flex-grow: 1; 
`

