  flex-grow: 4; 
  `

const BadgeListHeaderDropdownColumn = styled.div `
  flex-grow: 1; 
  `
const BadgeListRow = styled.div`
  background-color: aliceblue;
  padding: 0.5em;
  height: 500px;
`

const AvatarHeaderRow = styled.div `
  display: flex;
  justify-content: space-between;
  margin-bottom: 1.5em;
  column-gap: 5px;
  min-height: 60px;
`

