    // let DDSD_PITCH = 0x8;
    // let DDSD_PIXELFORMAT = 0x1000;

    const DDSD_MIPMAPCOUNT = 0x20000; // let DDSD_LINEARSIZE = 0x80000;
    // let DDSD_DEPTH = 0x800000;
    // let DDSCAPS_COMPLEX = 0x8;
    // let DDSCAPS_MIPMAP = 0x400000;
    // let DDSCAPS_TEXTURE = 0x1000;

