import React from 'react'
import styled from 'styled-components'
import { useEthers, getExplorerAddressLink, useEtherBalance, useContractFunction } from '@usedapp/core'
import { TransactionsList } from '../Transactions/History'
import { formatEther } from '@ethersproject/units'
import { BigNumber, utils } from 'ethers'
import { Colors, Shadows, Transitions } from '../../global/styles'
import { ShareIcon } from '../Transactions/Icons'
import { motion } from 'framer-motion'
import { Link } from '../base/Link'
import { RequirementsList } from './BadgeRequirementsList'
import { Contract } from '@ethersproject/contracts'


const formatter = new Intl.NumberFormat('en-us', {
  minimumFractionDigits: 4,
  maximumFractionDigits: 4,
})

const formatBalance = (balance: BigNumber | undefined) =>
  formatter.format(parseFloat(formatEther(balance ?? BigNumber.from('0'))))

export type BadgeOverviewModalProps = {
  setShowModal: React.Dispatch<React.SetStateAction<boolean>>
}

export const VisorsABI = [
  "function requestMint(uint256 visorId) public payable",
];


export const BadgeOverviewModal = ({ setShowModal }: BadgeOverviewModalProps) => {
  const { account, chainId, library } = useEthers()
  const balance = useEtherBalance(account)
  const visorInterface = new utils.Interface(VisorsABI)
  const VisorsContractAddress = '0x2f72199e3721c3b1eeb2913089c14c72ee9036b0'
  const signer = library?.getSigner();
  const contract = new Contract(VisorsContractAddress, visorInterface, signer);
  const { send } = useContractFunction(contract, 'requestMint', { signer: signer,  transactionName: 'Mint' })

  const doMint = async (visorId: number) => {
    let overrides = {
      value: ethers.utils.parseEther('0.0001')
    };
    send([1],overrides);
  }  
  if (account && chainId) {
    return (
      <ModalBackground onClick={() => setShowModal(false)}>
        <Modal
          onClick={(e) => e.stopPropagation()}
          layout
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0 }}
        >
          <TitleRow>
            Badge Details
            <ClosingButton onClick={() => setShowModal(false)}>+</ClosingButton>
          </TitleRow>
          <AccountInfo>
          <AccountAddress>Space Cadet</AccountAddress>
          <p>Portal Item : <b>Grey Visor</b></p>
          <p>Mint : <b>{account}</b></p>

            <LinkWrapper>
              <Link href={getExplorerAddressLink(account, chainId)} target="_blank" rel="noopener noreferrer">
                Show on etherscan
                <LinkIconWrapper>
                  <ShareIcon />
                </LinkIconWrapper>
              </Link>
              {window.isSecureContext && (
                <Link onClick={() => console.log(navigator.clipboard.writeText(account))}>Copy to clipboard</Link>
              )}
            </LinkWrapper>
            <BalanceWrapper>Balance: 4</BalanceWrapper>
            <MintButton>Mint</MintButton>
            <ApplyButton>Apply Item</ApplyButton>
          </AccountInfo>
          <HistoryWrapper>

            <RequirementsList />
          </HistoryWrapper>
        </Modal>
      </ModalBackground>
    )
  } else {
    setShowModal(false)
    return <div />
  }
}

export const Button = styled.button`
  display: grid;
  grid-auto-flow: column;
  grid-column-gap: 8px;
  align-items: center;
  padding: 5px;
  width: fit-content;
  height: 40px;
  font-size: 14px;
  line-height: 24px;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: ${Colors.Black[900]};
  border: 1px solid ${Colors.Black[900]};
  background-color: transparent;
  cursor: pointer;

  &:hover,
  &:focus {
    background-color: ${Colors.Black[900]};
    color: ${Colors.Yellow[100]};
  }
`

const MintButton = styled(Button)`
  margin-bottom: 5px;
  background-color: ${Colors.Yellow[100]};
`

const ApplyButton = styled(Button)`
  margin-bottom: 5px;
  background-color: ${Colors.Gray[300]};
`

const LinkWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 20px;
`

const LinkIconWrapper = styled.div`
  width: 12px;
  height: 12px;
`

const BalanceWrapper = styled.div`
  margin-top: 12px;
`

const HistoryWrapper = styled.div``

const AccountAddress = styled.p`
  font-weight: 700;
  margin-bottom: 10px;
`

const ClosingButton = styled.button`
  display: flex;
  position: absolute;
  top: 8px;
  right: 8px;
  justify-content: center;
  align-items: center;
  font-size: 24px;
  line-height: 1;
  width: 24px;
  height: 24px;
  transform: rotate(45deg);
  transition: ${Transitions.all};

  &:hover {
    color: ${Colors.Yellow[500]};
  }
`

const TitleRow = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  padding: 16px;
  width: 100%;
  font-size: 20px;
`

const AccountInfo = styled.div`
  display: block;
  margin: 16px;
  padding: 16px;
  border-radius: 10px;
  box-shadow: ${Shadows.main};
  background-color: ${Colors.White};
`

const Modal = styled(motion.div)`
  position: fixed;
  width: 600px;

  left: calc(50% - 300px);
  top: 100px;
  background-color: white;
  box-shadow: ${Shadows.main};
  border-radius: 10px;
  z-index: 3;
`

const ModalBackground = styled(motion.div)`
  top: 0;
  left: 0;
  position: fixed;
  width: 100%;
  height: 100%;
  margin: 0px;
  z-index: 2;
  background-color: rgba(235, 232, 223, 0.5);
`
