import axios, { AxiosResponse } from 'axios'
import { useEffect, useState } from 'react'
import { getSuccessfulTransactionCount, parseForBadgesForTypes, parseResponseForItems } from '../components/TokensList/BadgeHelper'
import { ChainId, useEthers } from '@usedapp/core'
import { getTransactionsFor } from '../components/TokensList/TransactionsAPI'

interface Badge {
    badgeName: string
    value: number
  }

export function useBadgeList(signerAddress: string|null|undefined, overrideChainId?: ChainId, tags?: string[]) {
  const { account, chainId: providerChainId } = useEthers()
  const [badges, setBadges] = useState<Badge[]>()
  const chainId = overrideChainId || providerChainId

  useEffect(() => {
    getTransactionsFor(signerAddress)
      .then(async (response) => {
        if (response) {
            const items = parseResponseForItems(response);
            if(items) {
                const successCount = getSuccessfulTransactionCount(items);
                const badges = parseForBadgesForTypes([successCount]);
                setBadges(badges);
            } else {
                // should do something with this case
                // in theory there should always be the defalt badge
                // if an account is connected
                setBadges(undefined);
            }
        } else {
          const errorMessage = 'Couldnt fetch transaction data';
          return Promise.reject(new Error(errorMessage))
        }
      })
      .catch((err) => {
        console.log(err)
        setBadges(undefined)
      })
  }, [account])
  return badges
}
