import { formatUnits } from '@ethersproject/units'
import uniswapToken from '@uniswap/default-token-list'
import { ChainId, ERC20Interface, useContractCall, useContractCalls, useEthers } from '@usedapp/core'
import React, { useEffect, useState } from 'react'
import { ERC1155Interface } from '../../interfaces/interfaces'
import styled from 'styled-components'
import { Colors } from '../../global/styles'
import { TextBold } from '../../typography/Text'
import { TokenIcon } from './TokenIcon'
import { Col, Container, Row } from 'react-bootstrap'
import { TokenSVGIcon } from './TokenSVGIcon'
import { useAppSelector, useAppDispatch } from '../../hooks'
import { increment } from '../../features/counter/counterSlice'
import { updatedSelectedItem } from './SelectedItemSlice'

import axios, { AxiosResponse } from 'axios'


const RANDOM_VISOR_COLORS = [
  'black',
  'red',
  'green',
  'gold',
  'blue',
  'grey',
  'hot-pink'
]


const ALL_BADGES: any = {
  metaPortal : {
    accountCreation : {
      greaterThan: [
        {
          value: 0,
          badgeName: 'Space Cadet',
        },
        {
          value: 0,
          badgeName: 'WAGMI Rank 1',
        },
      ]
    },
    successfulTransactions : {

      greaterThan: [
        {
          value: 25,
          badgeName: 'Explorer Rank 1',
        },
        {
          value: 50,
          badgeName: 'Explorer Rank 2',
        },
        {
          value: 75,
          badgeName: 'Explorer Rank 3',
        },
        {
          value: 100,
          badgeName: 'Sky Captain Rank 1',
        }
      ]
    }
  }

}

const initBadges: any[] = [];

export const getTransactionsFor = async (signerAddress: string): Promise<AxiosResponse|null>=> {
  console.log('signer address : ',signerAddress)
  const COVALENT_API_KEY = "ckey_4aed01d0d6e248a7a4ab27acb8a"
  const COVALENT_MAINNET = {
    url: "https://api.covalenthq.com/v1/1/",
  }

  const transactionsForAddress = 
  `${COVALENT_MAINNET.url}address/${signerAddress}/transactions_v2/?key=${COVALENT_API_KEY}`


  // Make a request for a user with a given ID
  return axios.get(transactionsForAddress)
    .then(function (response) {
      // handle success
      return response
    })
    .catch(function (error) {
      // handle error
      console.log(error);

      return null;
    });
}


export const parseResponseForItems =(response: AxiosResponse): (any[]|null) => {
  if(!response || !response.data || !response.data.data) {
    return null;
  }
  return response.data.data.items;
}

export const getSuccessfulTransactionCount =(items: any[]): number => {
  let successCount = 0;
  items.forEach((item) => {
    if(item.successful === true) {
      successCount++;
    }
  });
  return successCount;
}


export const parseForBadgesForTypes =(values: number[]): any[] => {
  return [
    ...extractBadgesForBadgeCriteria('successfulTransactions',values[0]),
    ...extractBadgesForBadgeCriteria('accountCreation',1)
  ]
}


export const extractBadgesForBadgeCriteria = (badgeType: string, valueAchieved: number): any[] => {
  let successCount = 0;
  let extractedBadges: any[] = [];

  ALL_BADGES['metaPortal'][badgeType]['greaterThan'].forEach((badgeItem: any) => {
    if(valueAchieved > badgeItem.value ) {
      extractedBadges.push(badgeItem);
    }
  });
  return extractedBadges;
}

function getTokenList(chainId?: ChainId) {
  return uniswapToken.tokens.filter((token) => token.chainId == chainId)
}



export function TokensList() {
  const { chainId, account } = useEthers()
  const tokenList = getTokenList(chainId)
  // const balances = useTokensBalance(tokenList, account);
  const [badges, setBadges] = useState(initBadges);
  const [visorColor, setVisorColor] = useState(RANDOM_VISOR_COLORS[0]);

  let mountedRef = true;

  useEffect(() => {
    const doAsync = async () => {
      init();
    }
    if(mountedRef) {
      doAsync();
    }
    return () => {
      mountedRef = false;
    }; 


  }, [mountedRef]);

  const init = async () => {
    console.log('account : ',account);
    if(!account || account === undefined){
      return;
    }
    console.log('awaiting response....')
   const response =  await getTransactionsFor(account);  
   console.log('axios : ',response);

    if(!response){
      return;
    }

    const items = parseResponseForItems(response);
    if(!items) {
      return;
    }
    const successCount = getSuccessfulTransactionCount(items);

    console.log('successes : ',successCount);
    const badges = parseForBadgesForTypes([successCount]);
    console.log('badges : ',badges);
    loadBadges(badges);
  }

    const selectedItemIndex = useAppSelector((state) => {
      return state.selectedItem.index;
    })
    const dispatch = useAppDispatch();

    const handleItemSelect =(index: number, balance: number, holders: number,name: string, address: string) =>  {

      dispatch (updatedSelectedItem(
        {
          index: index,
          balance: balance,
          holders: holders,
          name: name,
          address: address        
        }
      ))

    }


    const loadBadges =(transactionItems: any[]): void => {
      setBadges(transactionItems);
    }


  return (
    <div>
      {badges.length === 0 ? (<div>Not badges available...</div> ): (
        <div>
        <Row>
        {badges.map((badge: any, idx:number) => {
              return (
              <Col sm={4} md={2} lg={2} key={`parentSection-${idx}`}> 
              <Container>
              <Row>
                  <Col key={`SubSection-${idx}`}>
                    <TokenItem > 
                    <TokenIconContainer onClick={() => handleItemSelect(idx,idx ,11,`Element ${idx}`,'address')}>
                      <TokenSVGIcon src={''} alt={''}></TokenSVGIcon>
                      <TokenBalance> 
                        {formatUnits(0,0)}
                      </TokenBalance>
                    </TokenIconContainer>
                    <TokenName>{badge.badgeName}</TokenName>
                  </TokenItem>
                  </Col>
              </Row>
              </Container>
              </Col>
          )})}
        </Row>
      </div>
    )}
    </div>
  )
}

const List = styled.ul`
`

const ListItem = styled.li`
`

const TokenItem = styled.li`
  display: flex;
  flex-direction: column;
`

const TokenIconContainer = styled.div`
  display: flex;
  justify-content: center;
  width: 120px;
  height:120px;
  margin: 0.2em;
  border: 1px solid #ebebeb};
  background: rgb(60, 60, 60);
  background: rgba(149, 149, 149, 0.4);
  position: relative;

  &:hover,
  &:focus, 
  &:active {
    color: ${Colors.Yellow[100]};
    border-width: medium;
    border-color: ${Colors.Yellow[100]};
  }
`

const TokenName = styled(TextBold)`
  margin-left: 4px;
  font-size: 12px;

`

const TokenTicker = styled(TextBold)`
  grid-area: ticker;
  color: ${Colors.Gray[600]};
`

const TokenBalance = styled(TextBold)`
  position: absolute;
  bottom: 0;
  right: 0;
  padding:0.25em;
  font-size: 15px;
`
