import { Contract } from '@ethersproject/contracts'
import { formatUnits } from '@ethersproject/units'
import uniswapToken from '@uniswap/default-token-list'
import { ChainId, useContractFunction, useEthers } from '@usedapp/core'
import { ethers, utils } from 'ethers'
import React, { useState } from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import styled from 'styled-components'
import { Colors } from '../../global/styles'
import { useAppDispatch, useAppSelector } from '../../hooks'
import { RANDOM_VISOR_COLORS } from '../../portalHooks/BadgeHelper'
import { Badge } from '../../portalHooks/BadgeHelperTypes'
import { useBadgeList } from '../../portalHooks/useBadgeList'
import { TextBold } from '../../typography/Text'
import { BadgeOverviewModal, BadgeParams } from '../badge/BadgeOverviewModal'
import { updatedSelectedItem } from './SelectedItemSlice'
import { TokenSVGIcon } from './TokenSVGIcon'

declare const window: any;


export function TokensList() {

  const { chainId, account, library } = useEthers();
  const badges: Badge[]| undefined = useBadgeList(account);
  console.log('badges : ',badges)
  const [showModal, setShowModal] = useState(false)
  const [selectedBadgeParams, setSelectedBadgeParams] = useState<BadgeParams>()

  // let mountedRef = true;
  // const doMint = async (visorId: number) => {
  //   let overrides = {
  //     value: ethers.utils.parseEther('0.0001')
  //   };
  //   send([1],overrides);
  // }  
  // useEffect(() => {
  //   const doAsync = async () => {
  //     init();
  //   }
  //   if(mountedRef) {
  //     doAsync();
  //   }
  //   return () => {
  //     mountedRef = false;
  //   }; 
  // }, [mountedRef]);

    const selectedItemIndex = useAppSelector((state) => {
      return state.selectedItem.index;
    })
    const dispatch = useAppDispatch();

    const handleItemSelect =(index: number, balance: number, holders: number,name: string, address: string) =>  {

      dispatch (updatedSelectedItem(
        {
          index: index,
          balance: balance,
          holders: holders,
          name: name,
          address: address,
          color: index >= 0 ? RANDOM_VISOR_COLORS[index] : null,
        }
      ))

    }

    const handleShowModal =(index: number, badge: Badge) =>  {
      if(!badge || !badge.ui){
        return;
      }
      setSelectedBadgeParams({
        name: badge.ui.name,
        description: badge.ui.description,
        requirements: badge.ui.requirements,
        address: badge.ui.address,
      });

      setShowModal(!showModal);
    }


  return (
    <div>
      {
      (showModal && selectedBadgeParams) && <BadgeOverviewModal setShowModal={setShowModal} badgeParams={selectedBadgeParams} />
      }
      {badges && badges.length === 0 ? (<div>Not badges available...</div> ): (
        <div>
        <Row>
        {badges && badges.map((badge: Badge, idx:number) => {
              return (
              <Col sm={4} md={2} lg={2} key={`parentSection-${idx}`}> 
              <Container>
              <Row>
                  <Col key={`SubSection-${idx}`}>
                    <TokenItem > 
                    <TokenIconContainer onClick={() => setShowModal(!showModal)}>
                    {/* <TokenIconContainer onClick={() => handleItemSelect(idx,idx ,11,`Element ${idx}`,'address')}> */}
                      <TokenSVGIcon src={''} alt={''}></TokenSVGIcon>
                      <TokenBalance> 
                        {formatUnits(0,0)}
                      </TokenBalance>
                    </TokenIconContainer>
                        {badge.ui && <TokenName>{badge.ui.name}</TokenName>}                    
                        {/* <MintButton 
                      onClick={() => doMint(idx)}
                    >Mint</MintButton> */}
                  </TokenItem>
                  </Col>
              </Row>
              </Container>
              </Col>
          )})}
        </Row>
      </div>
    )}
    </div>
  )
}


export const Button = styled.button`
  display: grid;
  grid-auto-flow: column;
  grid-column-gap: 8px;
  align-items: center;
  width: fit-content;
  height: 40px;
  font-size: 14px;
  line-height: 24px;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: ${Colors.Black[900]};
  border: 1px solid ${Colors.Black[900]};
  background-color: transparent;
  cursor: pointer;

  &:hover,
  &:focus {
    background-color: ${Colors.Black[900]};
    color: ${Colors.Yellow[100]};
  }
`

const MintButton = styled(Button)`
  background-color: ${Colors.Yellow[100]};
`

const TokenItem = styled.li`
  display: flex;
  flex-direction: column;
`

const TokenIconContainer = styled.div`
  display: flex;
  justify-content: center;
  width: 120px;
  height:120px;
  margin: 0.2em;
  border: 1px solid #ebebeb};
  background: rgb(60, 60, 60);
  background: rgba(149, 149, 149, 0.4);
  position: relative;

  &:hover,
  &:focus, 
  &:active {
    color: ${Colors.Yellow[100]};
    border-width: medium;
    border-color: ${Colors.Yellow[100]};
  }
`

const TokenName = styled(TextBold)`
  margin-left: 4px;
  font-size: 12px;

`

const TokenTicker = styled(TextBold)`
  grid-area: ticker;
  color: ${Colors.Gray[600]};
`

const TokenBalance = styled(TextBold)`
  position: absolute;
  bottom: 0;
  right: 0;
  padding:0.25em;
  font-size: 15px;
`
