import { useEthers } from '@usedapp/core'
import { constants } from 'fs'
import React from 'react'
import { Col, Container, Row } from 'react-bootstrap'
import { useSelector } from 'react-redux'
import styled from 'styled-components'
import { selectCount } from '../../features/counter/counterSlice'
import { TextBold } from '../../typography/Text'
import { SendEthForm } from '../SendEthForm/SendEthForm'
import { SendItemForm } from '../SendItemForm/SendItemForm'
import { ShareIcon } from '../Transactions/Icons/ShareIcon'


import * as THREE from 'three'
import { Canvas, useFrame } from '@react-three/fiber'

import {SoldierModel} from '../../models/soldierModel';
import { OrbitControls } from '@react-three/drei'


export function ItemSelection() {
  const { chainId, account } = useEthers()


  const count = useSelector((state: any) => {
    return state.counter.value
  })

  const item = useSelector((state: any) => {
    return{index: state.selectedItem.index,
    balance: state.selectedItem.balance,
    holders: state.selectedItem.holders,
    name: state.selectedItem.name,
    address: state.selectedItem.address,}
  })

  console.log('item :',item);


  return account ?  (
          <Container>

        <Row>
              <Col>
                <Canvas>
                <OrbitControls/>
                <ambientLight />
                <pointLight position={[10, 10, 10]} />


                {/* <Box position={[-1.2, 0, 0]} /> */}
                <SoldierModel position={[1.2, 0, 0]} />
                </Canvas> 
              </Col>
              <Col>
                  <SelectionTitle>{item.name} </SelectionTitle>
                  <ItemAddressLink>
                  <ItemAddressWrapper>
                    {item.address && <p>{item.address}</p>}
                    {item.address && <LinkIconWrapper> <ShareIcon /></LinkIconWrapper>}
                  </ItemAddressWrapper>
                  </ItemAddressLink>

              </Col>
            </Row>
          </Container>
  ) : <Container></Container>
}

const LinkIconWrapper = styled.div`
  width: 12px;
  height: 12px;
  margin-left:5px;
`


const NoItemSelected = styled(TextBold)`
  margin-left: 4px;
  font-size: 14px;
`

const ItemDetail = styled(TextBold)`
  margin-left: 4px;
  font-size: 14px;
`

const ItemAddressWrapper = styled.div`
  display: flex;
  font-size: 15px;
`

const ItemAddressLink = styled.a`
`
const ItemPreview = styled(TextBold)`
width:60%;
`

const TokenBalance = styled(TextBold)`
  position: absolute;
  bottom: 0;
  right: 0;
  padding:0.25em;
  font-size: 15px;
`
const SectionBreak = styled.div`
  margin-bottom: 20px;
`

const SelectionTitle = styled.h2`
`
