import { AxiosResponse } from "axios";
import { TransactionLevel, ParseParams, CriteriaComparions, Badge } from "./BadgeHelperTypes";


export const parseResponseForItems =(response: AxiosResponse): (any[]|null) => {
    if(!response || !response.data || !response.data.data) {
      return null;
    }
    return response.data.data.items;
  }

  export const getSuccessfulTransactionCount =(items: any[]): number => {
    let successCount = 0;
    items.forEach((item) => {
      if(item.successful === true) {
        successCount++;
      }
    });
    return successCount;
  }


  export const parseForBadgesForTypes =(values: number[]): any[] => {
    return [
      ...extractBadgesForBadgeCriteria('successfulTransactions',values[0]),
      ...extractBadgesForBadgeCriteria('accountCreation',1)
    ]
  }


  export const extractBadgesForBadgeCriteria = (badgeType: string, valueAchieved: number): any[] => {
    let successCount = 0;
    let extractedBadges: any[] = [];

    ALL_BADGES['metaPortal'][badgeType]['greaterThan'].forEach((badgeItem: any) => {
      if(valueAchieved > badgeItem.value ) {
        extractedBadges.push(badgeItem);
      }
    });
    return extractedBadges;
  }

  export const RANDOM_VISOR_COLORS = [
    'black',
    'white',
    'grey',
    'green',
    'blue',
    'hot-pink',
    'purple',
    'silver',
    'bronze',
    'gold'
  ]

  export const ALL_BADGES: any = {
    metaPortal : {
      accountCreation : {
        greaterThan: [
          {
            value: 0,
            badgeName: 'Space Cadet',
          },
          {
            value: 0,
            badgeName: 'GM Rank 1',
          },
        ]
      },
      successfulTransactions : {

        greaterThan: [
          {
            value: 25,
            badgeName: 'Explorer Rank 1',
          },
          {
            value: 50,
            badgeName: 'Explorer Rank 2',
          },
          {
            value: 75,
            badgeName: 'Explorer Rank 3',
          },
          {
            value: 100,
            badgeName: 'Sky Captain Rank 1',
          }
        ]
      }
    }
  }

  export const calculateFrequencyForKeyValueMatches =(
    transactions: any[], 
    key: string, 
    value: number, 
    valueMatching: boolean
    ) : number => {
      if(valueMatching) {
        // matches if 'key' found && key maps to 'value'
        return getKeyValueMatchCount(transactions, key, value);
      } else {
          // matches if 'key' found
          return getKeyMatchCount(transactions, key);
      }
  }

  export const getKeyValueMatchCount = (items: any[], key: string, value: any) =>  {
      let count = 0;
      items.forEach((item) => {
        if(item[key] && item[key] === value) {
          count++;
        }
      });
      return count;
  }

  export const getKeyMatchCount = (items: any[], key: string) =>  {
    let count = 0;
    items.forEach((item) => {
      if(item[key]) {
        count++;
      }
    });
    return count;
}


export const getValidBadges = (badges: Badge[], transactions: any[]) : Badge[] =>  {
  let validBadges: Badge[] = [];
  badges.forEach((badge: Badge) => {
    if(validateBadgeAgainstTransactions(badge,transactions)){
      validBadges.push(badge);
    }
  });
  return validBadges;
}


export const validateBadgeAgainstTransactions = (badge: Badge, transactions: any[]): boolean =>  {
  let isValid = false;
  switch(badge.parseRules.level) {
    case TransactionLevel.Root :
        // compare rules with trnsaction list
        isValid = executeRootLevelChecksForBadge(badge.parseRules,transactions);
      return isValid;
    default:
      return false;
  }
}


export const executeRootLevelChecksForBadge = (rules: ParseParams, transactions: any[]): boolean =>  {
  // defaulting to Key Match frequency comparisons
  return executeFrequencyRuleForBadge(
    rules.keyValueObject.name, 
    rules.keyValueObject.value,
    rules.criteria.comparison,
    rules.criteria.value,
    rules.useKeyValueMatching,
    transactions
    );
}


export const executeFrequencyRuleForBadge = (
  key: string, 
  value: any, 
  criteriaRule: CriteriaComparions,
  criteriaValue: number,
  isValueMathing: boolean,
  transactions: any[], ): boolean =>  {
  // defaulting to Key Match frequency comparisons
  let isValid = false;
  const frequency = calculateFrequencyForKeyValueMatches(transactions, key, value, isValueMathing);

  switch(criteriaRule) {
    case CriteriaComparions.GT : 
      return frequency > criteriaValue;

    case CriteriaComparions.LT : 
      return frequency < criteriaValue;

    default:
      return false;
  }

  return isValid;
}
